import codecs
import sys
import re



def normalizeArabic(text):
    text = re.sub("[Ø¥Ø£Ù±Ø¢Ø§]", "Ø§", text)
    text = re.sub("Ù‰", "ÙŠ", text)
    text = re.sub("Ø¤", "Ø¡", text)

    return(text)

def strip_tatweel(text):
    return text.replace(u'\u0640', '')

FATHATAN = u'\u064b'
DAMMATAN = u'\u064c'
KASRATAN = u'\u064d'
FATHA = u'\u064e'
DAMMA = u'\u064f'
KASRA = u'\u0650'
SHADDA = u'\u0651'
SUKUN = u'\u0652'
TASHKEEL = (FATHATAN, DAMMATAN, KASRATAN, FATHA, DAMMA, KASRA, \
SUKUN, SHADDA)

def strip_tashkeel(text):

    for char in TASHKEEL:
        text = text.replace(char, '')
    return text



