################ Remove Emojs only ###################################
def remove_emoji(data):

    if not data:
        return data
    try:
    # UCS-4
        patt = re.compile(u'([\U00002600-\U000027BF])|([\U0001f300-\U0001f64F])|([\U0001f680-\U0001f6FF])')
    except re.error:
    # UCS-2
        patt = re.compile(u'([\u2600-\u27BF])|([\uD83C][\uDF00-\uDFFF])|([\uD83D][\uDC00-\uDE4F])|([\uD83D][\uDE80-\uDEFF])')
    return patt.sub(' ', data)
#####################Remove all symbols#############################




def remove_allsymbols(content):
    for c in all_symbols:
#        if u'_' not in c :
            content=content.replace(c,' ')
#        else:
            content=content
    return content



def remove_urls(text):
    l=[]
    words=text.split("\n")
    for word in words:
        #remove_websites links
        word = re.sub(r'[a-z]*[:.]+\S+', " ", word)
        #remove @acconts
        word = re.sub(r'\s@+[^\s]*', " ", word)

        if len(word)<=1:
            l.append(" ")
        else:
           if len(word)> 1:
                l.append(word)
    out_text="\n".join(l)

    return out_text

#




def remove_stopwords(text):
    stopwords_file= codecs.open("stopwords.txt","r","utf-8")
    stopwords= stopwords_file.read()
    stopwords_list= stopwords.split("\n")
    list_words= text.split(" ")
    text=""
    listx=[]
    for x in list_words:
       if x not in stopwords_list:
        listx.append(x)
       if x in stopwords_list:
        listx.append(" ")
    text = " ".join(listx)
    text= text.replace("  "," ")



    return text




file1= codecs.open('input_data.txt','r','utf-8')
content=file1.read()
file2= codecs.open('output_data.txt','w+','utf-8')


content = remove_emoji(content)

content= remove_allsymbols(content)

content = remove_urls(content)

content = normalizeArabic(content)

content = strip_tatweel(content)

content = strip_tashkeel (content)

content = remove_stopwords(content)

file2.write(content)
print ("Done")
file1.close()
file2.close()
